import React from "react"
import { useState } from "react"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"

export default function LoginForm() {
    const [inputs, setInputs] = useState({})

    const handleChange = (event) => {
      const name = event.target.name
      const value = event.target.value
      setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = (event) => {
        event.preventDefault()
        console.log(inputs)
    }

    const containerStyling = {
        display:"flex",
        flexDirection:"column",
    }

    const mainTitleStyling = {
        textAlign:"left",
        margin:"3em auto 2em 0"
    }

    const buttonStyling = {
        background:"#303e4d",
        width:"200px",
        padding:"0.5em 1em",
        border:"none",
        borderRadius:"10px",
        color:"#fffdf8",
        fontFamily:"montserrat, sans-serif"
    }

    return (
        <div className="login-form-component" style={containerStyling}>
            <h3 style={mainTitleStyling}>Welcome Back!</h3>
            <form onSubmit={handleSubmit}>
                <label> Email
                <input 
                    type="email" 
                    name="email"
                    placeholder={`${<FontAwesomeIcon icon="fa-regular fa-envelope" />} Email`}
                    value={inputs.email || ""} 
                    onChange={handleChange}
                />
                </label>
                <label> Password
                <input 
                    type="password" 
                    name="password"
                    placeholder={`${<FontAwesomeIcon icon="fa-regular fa-lock" />} Password`}
                    value={inputs.password || ""} 
                    onChange={handleChange}
                />
                </label>
                <input type="submit" placeholder="Login" style={buttonStyling}/>
            </form>
        </div>
    )
}
