import React, { useEffect, useState } from "react";
import { Navigate, Outlet } from "react-router-dom";

const isAuthenticated = () => {
  // Check if the user is authenticated
  const isLoggedIn = localStorage.getItem("user_id");
  return isLoggedIn !== null;
};

export default function ProtectedRoute() {
  const [hasAuthRoutes, setHasAuthRoutes] = useState(false);

  const isAuth = isAuthenticated();

  useEffect(() => {
    const checkAuthRoutes = () => {
      const authRouteLinks = document.querySelectorAll('a[href="/"], a[href="/login"], a[href="/signup"]');
      setHasAuthRoutes(authRouteLinks.length > 0);
    };

    if (!hasAuthRoutes) {
      document.addEventListener("DOMContentLoaded", checkAuthRoutes);
    }

    return () => {
      document.removeEventListener("DOMContentLoaded", checkAuthRoutes);
    };
  }, [hasAuthRoutes]);

  if (!isAuth && hasAuthRoutes) {
    return <Navigate to={document.location.pathname} />;
  } else if (isAuth) {
    return <Outlet />;
  } else {
    return null; // or any loading indicator
  }
}
