import React from "react"
import { useState, useEffect } from "react"
import { useNavigate, Link, useLocation } from "react-router-dom"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faEnvelope, faLock } from "@fortawesome/free-solid-svg-icons"

export default function LoginForm() {
    const [inputs, setInputs] = useState({})
    const [failedLogin, setFailedLogin] = useState(false)
    const navigate = useNavigate()

    // useEffect(() => {
    //     // Check if user is already authenticated when the component mounts
    //     const isAuthenticated = localStorage.getItem("isLoggedIn")
    //     if (isAuthenticated) {
    //         navigate("/job")
    //     }
    // }, [])

    const handleChange = (event) => {
      const name = event.target.name
      const value = event.target.value
      setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = async (event) => {
        event.preventDefault()
        // try {
        //     const response = await fetch("http://127.0.0.1:5000/login", {
        //         method: "POST",
        //         credentials: "include",
        //         headers: {
        //             "Content-Type": "application/json"
        //         },
        //         body: JSON.stringify(inputs)
        //     })

        //     if (response.ok) {
        //         const responseData = await response.json()
        //         // Store the user ID in local storage
        //         console.log(responseData)
        //         navigate("/job")
        //         localStorage.setItem("user_id", responseData.user_id)
        //         setFailedLogin(false)
        //         setInputs({})
        //     } else {
        //         console.error("Login failed")
        //         setFailedLogin(true)
        //     }
        // } catch (error) {
        //     console.error("Error:", error)
        //     navigate("/*")
        // }
        try {
            const nextUrl = new URLSearchParams(location.search).get('next') || '/job';
            const response = await fetch(`http://127.0.0.1:5000/login?next=${encodeURIComponent(nextUrl)}`, {
                method: "POST",
                credentials: "include",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(inputs)
            });

            if (response.ok) {
                const responseData = await response.json();
                // Store the user ID in local storage
                console.log(responseData);
                navigate("/job");
                localStorage.setItem("user_id", responseData.user_id);
                setFailedLogin(false);
                setInputs({});
            } else {
                console.error("Login failed");
                setFailedLogin(true);
            }
        } catch (error) {
            console.error("Error:", error);
            navigate("/*");
        }

    }

    const containerStyling = {
        display: "flex",
        flexDirection: "column",
        justifyContent:"center",
        alignItems:"center"
    }

    const mainTitleStyling = {
        textAlign: "center",
        fontSize:"1.75rem",
        textDecoration:"underline",
        margin: "2em auto 1.5em auto",
    }

    const inputStyling = {
        margin:"1em auto 3em 0",
        fontSize:"0.9rem",
        padding:"1.25em 1.25em 1.25em 2.5em",
        width:"270px",
        borderRadius:"10px",
        background:"#fcf7eb",
        boxShadow: "2px 2px 4px rgba(0, 0, 0, 0.2)",
        border:"0.5px solid #303e4d"
    }

    const iconStyling = {
        position: "absolute", 
        top: "37%", 
        left: "10px", 
        transform: "translateY(-50%)",
        opacity:"70%"
    }

    const buttonStyling = {
        background: "#303e4d",
        margin:"4em auto 1em auto",
        width: "270px",
        padding: "0.6em 2em",
        border: "none",
        borderRadius: "10px",
        color: "#fffdf8",
        fontFamily: "montserrat, sans-serif",
        fontSize:"1.25rem",
        cursor:"pointer"
    }

    const errorMessageStyling = {
        fontSize:"0.9rem",
        margin:"0 auto",
        fontWeight:"600",
        opacity:"0%"
    }

    const footerStyling = {
        margin:"3em auto 1em auto",
        fontSize:"0.9rem"
    }

    const linkStyling = {
        color:"#303e4d"
    }

    return (
        <div className="login-form-component" style={containerStyling}>
            <h3 style={mainTitleStyling}>Welcome Back!</h3>
            <form onSubmit={handleSubmit} style={containerStyling} action={`/login?next=${encodeURIComponent(location.search)}`}>
                <label> Email
                    <div style={{position: "relative" }}>
                        <FontAwesomeIcon icon={faEnvelope} style={iconStyling} />
                        <input 
                            style={inputStyling}
                            type="email" 
                            name="email"
                            placeholder="Email"
                            value={inputs.email || ""} 
                            onChange={handleChange}
                            required
                        />
                    </div>
                </label>
                <label> Password
                    <div style={{position: "relative" }}>
                        <FontAwesomeIcon icon={faLock} style={iconStyling} />
                        <input 
                            style={inputStyling}
                            type="password" 
                            name="password"
                            placeholder="Password"
                            value={inputs.password || ""} 
                            onChange={handleChange}
                            required
                        />
                    </div>
                </label>
                <p style={{...errorMessageStyling, opacity: failedLogin ? "100%" : "0%" }}>
                    Invalid Username or password. Try again!
                </p>
                <input type="submit" value="Login" style={buttonStyling} className="form-btn"/>
                <p style={footerStyling}>
                    Need an account? Get started <Link to={"/signup"} style={linkStyling}>here</Link>
                </p>
            </form>
        </div>
    )
}
