import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser, faEnvelope, faLock } from "@fortawesome/free-solid-svg-icons"

export default function SignUpForm() {
    const [inputs, setInputs] = useState({});

    const handleChange = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setInputs(values => ({ ...values, [name]: value }));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
    };

    const containerStyle = {
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
    };

    const inputStyle = {
        margin: "1em 0",
        padding: "0.5em 1em",
        borderRadius: "5px",
        border: "1px solid #ccc",
        width: "250px",
    };

    return (
        <div className="signup-form-component" style={containerStyle}>
            <h3>Create an Account</h3>
            <form onSubmit={handleSubmit}>
                <label>
                    Username
                    <input
                        type="text"
                        name="username"
                        placeholder={<FontAwesomeIcon icon={faUser} />} // Use JSX directly
                        value={inputs.username || ""}
                        onChange={handleChange}
                        style={inputStyle}
                    />
                </label>
                <label>
                    Email
                    <input
                        type="email"
                        name="email"
                        placeholder={<FontAwesomeIcon icon={faEnvelope} />} // Use JSX directly
                        value={inputs.email || ""}
                        onChange={handleChange}
                        style={inputStyle}
                    />
                </label>
                <label>
                    Password
                    <input
                        type="password"
                        name="password"
                        placeholder={<FontAwesomeIcon icon={faLock} />} // Use JSX directly
                        value={inputs.password || ""}
                        onChange={handleChange}
                        style={inputStyle}
                    />
                </label>
                <input type="submit" value="Sign Up" style={{ ...inputStyle, background: "#303e4d", color: "#fff", cursor: "pointer" }} />
            </form>
        </div>
    );
}
