import React from "react"
import { faFolder } from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

export default function Header(props) {
    const buttonStyling = {
        padding: "0.25em 0.75em",
        border: "1px solid #303E4D",
        background: "#FFC445",
        color: "#303E4D"
    }
    const logoStyling = {
        border:"none",
        width:"40px"
    }

    return (
        <div className="header-component">
           {props.icon? 
                <button 
                    style={logoStyling}
                    aria-label={`button that redirects to Home page `}>
                        <img src={props.icon} alt="JobCache logo"/>
                </button> 
            : 
                <button 
                    aria-label={`button that redirects to Jobs page`}
                    style={buttonStyling}> 
                        <FontAwesomeIcon icon={faFolder} /> JOBS 
                </button>
            }
            <button aria-label={`button that redirects to ${props.secondButtonText} page`} style={buttonStyling}>
                {props.secondButtonText}
            </button>
            <button aria-label={`button that redirects to ${props.thirdButtonText} page`} style={buttonStyling}>
                {props.thirdButtonText}
            </button>
        </div>
    )
}
