import React from "react"
import { useState, useEffect } from "react"
import { Link } from 'react-router-dom'
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faPlusCircle, faChevronRight } from "@fortawesome/free-solid-svg-icons"

export default function JobList() {
    const [applicationList, setApplicationList] = useState({})
    const getApplications = async (event) => {
        try {
            const response = await fetch("/job", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json"
                }
            })

            if (response.ok) {
                const data = await response.json()
                setApplicationList(data)
            } else {
                console.error("Application list retrieval failed")
            }
        } catch (error) {
            console.error("Error:", error)
        }
    }

    useEffect(() => {
        getApplications()
    }, [])

    const containerStyling = {
        margin:"4em auto 1em auto"
    }

    const infoContainerStyling = {
        display:"flex",
        flexDirection:"row"
    }

    const mainIconStyling = {
        margin:"auto 0 auto auto",
        cursor:"pointer"
    }

    const mainTitleStyling = {
        margin:"auto auto auto 0",
        textAlign:"left",
        fontWeight:"500"
    }

    const applicationContainerStyling = {
        background:"#fcf7eb",
        borderRadius:"10px",
        border:"0.5px solid #303e4d",
        boxShadow: "2px 2px 4px rgba(0, 0, 0, 0.2)",
        margin:"1.5em auto",
        overflow:"scroll",
        maxHeight:"430px"
    }

    const applicationStyling = {
        background:"#fcf7eb",
        borderBottom:"0.5px solid #303e4d",
        padding:"0.75em 0.5em",
        display:"flex"
    }

    const applicationIconStyling = {
        margin:"auto"
    }

    const linkStyling = {
        textDecoration:"none",
        margin:"auto 0 auto auto",
        color:"#303e4d",
        fontSize:"1.25rem"
    }

    const statusStyling = {
        background:"#ffc445",
        borderRadius:"20px",
        padding:"0.35em 1em",
        width:"min-content",
        fontSize:"0.8rem",
        margin:"auto"
    }

    const applicationInfoStyling = {
        display:"flex",
        flexDirection:"column",
        margin:"auto",
        justifyContent:"center",
        alignItems:"center"
    }

    const InfoPositionStyling = {
        margin:"0.25em auto",
        fontSize:"0.9rem",
        fontWeight:"600"
    }

    const InfoCompanyStyling = {
        margin:"0.25em auto",
        fontSize:"0.9rem",
        fontWeight:"400"
    }

    return (
        <div className="job-list-component" style={containerStyling}>
            <div style={infoContainerStyling}>
                <h3 style={mainTitleStyling}>Job Applications</h3>
                <Link to="newJob" style={linkStyling}>
                    <FontAwesomeIcon icon={faPlusCircle} style={mainIconStyling} />
                </Link>
            </div>
            <div style={applicationContainerStyling}>
                <div style={applicationStyling}>
                    <div style={applicationInfoStyling}>
                        <p style={InfoPositionStyling}>Software Engineer</p>
                        <p style={InfoCompanyStyling}>@Google</p>
                    </div>
                    <div style={statusStyling}>Pending</div>
                    <FontAwesomeIcon icon={faChevronRight} style={applicationIconStyling}/>
                </div>
                <div style={applicationStyling}>
                    <div style={applicationInfoStyling}>
                        <p style={InfoPositionStyling}>Frontend Engineer</p>
                        <p style={InfoCompanyStyling}>@Apple</p>
                    </div>
                    <div style={{...statusStyling, background:"#58D308"}}>Accepted</div>
                    <FontAwesomeIcon icon={faChevronRight} style={applicationIconStyling}/>
                </div>
            </div>
        </div>
    )
}
