import React from "react"
import { useState } from "react"
import { useNavigate } from "react-router-dom"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { faEnvelope, faLock } from "@fortawesome/free-solid-svg-icons"

export default function LoginForm() {
    const [inputs, setInputs] = useState({})
    const [failedAttempt, setFailedAttempt] = useState(true)
    const navigate = useNavigate()

    const handleChange = (event) => {
      const name = event.target.name
      const value = event.target.value
      setInputs(values => ({...values, [name]: value}))
    }

    const handleSubmit = async (event) => {
        event.preventDefault()

        try {
            const response = await fetch("/login", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json"
                },
                body: JSON.stringify(inputs)
            })

            if (response.ok) {
                navigate("/job")
                setFailedAttempt(false)
            } else {
                console.error("Login failed")
                setFailedAttempt(true)
            }
        } catch (error) {
            console.error("Error:", error)
            navigate("/*")
        }
    }

    const containerStyling = {
        display: "flex",
        flexDirection: "column",
        justifyContent:"center",
        alignItems:"center"
    }

    const mainTitleStyling = {
        textAlign: "center",
        fontSize:"1.75rem",
        textDecoration:"underline",
        margin: "2em auto 1.5em auto",
    }

    const inputStyling = {
        margin:"1em auto 3em 0",
        fontSize:"0.9rem",
        padding:"1.25em 1.25em 1.25em 2.5em",
        width:"270px",
        borderRadius:"10px",
        background:"#fcf7eb",
        boxShadow: "2px 2px 4px rgba(0, 0, 0, 0.2)",
        border:"0.5px solid #303e4d"
    }

    const iconStyling = {
        position: "absolute", 
        top: "37%", 
        left: "10px", 
        transform: "translateY(-50%)",
        opacity:"70%"
    }

    const buttonStyling = {
        background: "#303e4d",
        margin:"4em auto 1em auto",
        width: "270px",
        padding: "0.6em 2em",
        border: "none",
        borderRadius: "10px",
        color: "#fffdf8",
        fontFamily: "montserrat, sans-serif",
        fontSize:"1.25rem",
        cursor:"pointer"
    }

    const errorMessageStyling = {
        fontSize:"1.5rem",
        margin: "2em auto 3em auto",
        fontWeight:"600"
    }

    return (
        <div className="login-form-component" style={containerStyling}>
           {failedAttempt ?
                <p style={errorMessageStyling}>Invalid Username or password. Try again!</p>
            :
                <h3 style={mainTitleStyling}>Welcome Back!</h3>
            } 
            <form onSubmit={handleSubmit} style={containerStyling}>
                <label> Email
                    <div style={{position: "relative" }}>
                        <FontAwesomeIcon icon={faEnvelope} style={iconStyling} />
                        <input 
                            style={inputStyling}
                            type="email" 
                            name="email"
                            placeholder="Email"
                            value={inputs.email || ""} 
                            onChange={handleChange}
                            required
                        />
                    </div>
                </label>
                <label> Password
                    <div style={{position: "relative" }}>
                        <FontAwesomeIcon icon={faLock} style={iconStyling} />
                        <input 
                            style={inputStyling}
                            type="password" 
                            name="password"
                            placeholder="Password"
                            value={inputs.password || ""} 
                            onChange={handleChange}
                            required
                        />
                    </div>
                </label>
                <p style={{...errorMessageStyling, opacity: failedAttempt ? "100%" : "0%" }}>
                    Invalid Username or password. Try again!
                </p>
                <input type="submit" value="Login" style={buttonStyling}/>
            </form>
        </div>
    )
}
