import React from "react"
import Header from "../components/Header"
import { faSun, faBoltLightning } from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useEffect } from "react"

export default function Home() {
    useEffect(() => {
        document.title = "JobCache - Daily Motivation"
    }, []) 

    //api functions



    const infoSectionStyling = {
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        flexDirection:"column",
        margin:"4em auto 1em auto"
    }


    const messageContainerStyling = {
        background:"#fcf7eb",
        borderRadius:"10px",
        border:"0.5px solid #303e4d",
        padding:"0.5em 1em",
        boxShadow: "2px 2px 4px rgba(0, 0, 0, 0.2)",
        margin:"auto",
    }

    const titleStyling = {
        fontWeight:"500",
        fontSize:"1.25rem"
    }

    return (
        <div className="motivate-page">
            <Header 
                secondButtonText={"MOTIVATE"}
                thirdButtonText={"LOG OUT"}  
            />
            <div className="motivate-info-section" style={infoSectionStyling}>
                <FontAwesomeIcon icon={faSun} />
                <h4 style={titleStyling}>Daily Motivation</h4>
                <div className="quote-container" style={messageContainerStyling}>
                    <p>Check back tomorrow for a quote</p>
                </div>
                <div className="motivate-info-section-divider"></div>
                <FontAwesomeIcon icon={faBoltLightning} />
                <h4>Daily Opportunity</h4>
                <div className="opportunity-container">
                    <h5>Software Engineer</h5>
                    <p>@IBM</p>
                    <p>www.ibm.ca/jobs/id=wfh233ned</p>
                </div>
            </div>
        </div>
    )
}
