import React from "react"
import Header from "../components/Header"
import { faSun, faBolt} from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { useEffect, useRef } from "react"

export default function Home() {
    let viewedQuote = useRef(false)

    useEffect(() => {
        document.title = "JobCache - Daily Motivation"
    }, []) 

    const getQuote = async () => {
        try {
          const response = await fetch('quotes.json')
          const data = await response.json()
          const randomIndex = Math.floor(Math.random() * data.length)
          const randomQuote = data["quotes"[randomIndex]]
          console.log(randomQuote.quote + ' - ' + randomQuote.author)
          localStorage.setItem('lastQuoteTime', Date.now())
        } catch (error) {
          console.error('Error:', error)
        }
    }

    useEffect(() => {
        const lastQuoteTime = localStorage.getItem('lastQuoteTime')
        const currentTime = Date.now()
        const timeElapsed = currentTime - lastQuoteTime

        if (!viewedQuote.current || timeElapsed >= 1000 * 60 * 60 * 16) {
          getQuote()
          viewedQuote.current = true
        }

        const intervalId = setInterval(() => {
          getQuote()
        }, 1000 * 60 * 60 * 16 - timeElapsed)

        return () => clearInterval(intervalId)
    }, [])


    const infoSectionStyling = {
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        flexDirection:"column",
        margin:"4em auto 1em auto"
    }

    const messageContainerStyling = {
        background:"#fcf7eb",
        borderRadius:"10px",
        border:"0.5px solid #303e4d",
        padding:"0.5em 1em",
        boxShadow: "2px 2px 4px rgba(0, 0, 0, 0.2)",
        margin:"auto",
        fontSize:"0.9rem",
        minWidth:"280px",
        textAlign:"center"
    }

    const titleStyling = {
        fontWeight:"500",
        fontSize:"1.17rem"
    }

    const iconStyling = {
        fontSize:"1.5rem",
        color:"#303e4d"
    }

    const dividerStyling = {
        height:"2px",
        background:"#303e4d",
        margin:"3em auto",
        width:"90%",
        borderRadius:"15px"
    }

    const positionTitleStyling = {
        fontSize:"0.9rem",
        margin:"1em auto",
        fontWeight:"500"
    }

    return (
        <div className="motivate-page">
            <Header 
                secondButtonText={"MOTIVATE"}
                thirdButtonText={"LOG OUT"}  
            />
            <div className="motivate-info-section" style={infoSectionStyling}>
                <FontAwesomeIcon icon={faSun} style={iconStyling}/>
                <h4 style={titleStyling}>Daily Motivational Quote</h4>
                <div className="quote-container" style={messageContainerStyling}>
                    <p>Check back tomorrow for a quote</p>
                </div>
                <div className="motivate-info-section-divider" style={dividerStyling}></div>
                <FontAwesomeIcon icon={faBolt} style={{...iconStyling, rotate:"-10deg"}}/>
                <h4 style={titleStyling}>Daily Opportunity</h4>
                <div className="opportunity-container" style={messageContainerStyling}>
                    <h5 style={positionTitleStyling}>Software Engineer</h5>
                    <p>@IBM</p>
                    <p>www.ibm.ca/jobs/id=wfh233ned</p>
                </div>
            </div>
        </div>
    )
}
