import React from "react";
import { Navigate, Outlet, useParams } from "react-router-dom";

const isAuthenticated = () => {
  // Check if the user is authenticated
  const isLoggedIn = localStorage.getItem("user_id");
  return isLoggedIn !== null;
};

export default function ProtectedRoute() {
  // Get the current page ID using useParams
  const { id } = useParams();

  // Check if the user is authenticated
  const isAuth = isAuthenticated();

  // Allow access to the login page if the user is not authenticated and the current page is the login page
  if (!isAuth && (id === "login" || id === "/" || id === "signup")) {
    return <Outlet />
  }
  else {
    <Navigate to="/signup" replace />
  }

  // If the user is not authenticated, redirect them to the signup page
  //return !isAuth ? <Navigate to="/signup" replace /> : <Outlet />;
}
