import React from "react"
import Header from "../components/Header"
import quotesData from "../quotes.json"
import { TailSpin } from 'react-loader-spinner'
import { faSun, faBolt} from "@fortawesome/free-solid-svg-icons"
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome"
import { useEffect, useRef, useState } from "react"

export default function Home() {
    useEffect(() => {
        document.title = "JobCache - Motivation"
    }, [])

    const sortResults = (data) =>{
        return data.filter(current => (
            !current.role.toLowerCase().includes("director") && 
            !current.role.toLowerCase().includes("sales")
        ))
    }



    const viewedQuote = useRef(true)
    const [randomQuote, setRandomQuote] = useState(() => {
        // Retrieve the quote from local storage on initial render
        const storedQuote = localStorage.getItem("randomQuote")
        return storedQuote ? JSON.parse(storedQuote) : null
    })

    const getQuote = () => {
        const quotes = quotesData.quotes
        const randomIndex = Math.floor(Math.random() * quotes.length)
        const quote = quotes[randomIndex]
        setRandomQuote(quote)
        localStorage.setItem("randomQuote", JSON.stringify(quote))
        localStorage.setItem("lastQuoteTime", Date.now())
    }

    useEffect(() => {
        const lastQuoteTime = localStorage.getItem('lastQuoteTime')
        const currentTime = Date.now()
        const timeElapsed = currentTime - lastQuoteTime
        //refreshes every 16 hours
        if (!viewedQuote.current || timeElapsed >= 1000 * 60 * 60 * 16) {
          getQuote()
          viewedQuote.current = true
        }

        const intervalId = setInterval(() => {
          getQuote()
        }, 1000 * 60 * 60 * 16 - timeElapsed)

        return () => {
            clearInterval(intervalId)
            viewedQuote.current = false
        }
    }, [])

    const viewedOpportunity = useRef(true)
    const [jobOpportunity, setJobOpportunity] = useState(() => {
        // Retrieve the job opportunity from local storage on initial render
        const storedJobOpportunity = localStorage.getItem("jobOpportunity")
        return storedJobOpportunity ? JSON.parse(storedJobOpportunity) : null
    })

    const getJobOpportunity = async () => {
        try {
            const response = await fetch("/job/motivate", {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "Cache-Control": "no-cache",
                    "Authorization":"Token f7d5d074f4ed5b3239030832fca273cc4ab78fde",
                    "Accept":"application/json",
                },
            })

            if (response.ok) {
                const responseData = await response.json()
                setJobOpportunity(sortResults(responseData.results)[0])
                localStorage.setItem("jobOpportunity", JSON.stringify(sortResults(responseData.results)[0]))
                localStorage.setItem("lastJobOpportunityTime", Date.now())
            } else {
                console.error("failed to retrieve job opportunity")
            }
        } catch (error) {
            console.error("Error:", error)
        }
    }

    useEffect(() => {
        const lastJobOpportunityTime = localStorage.getItem('lastJobOpportunityTime')
        const currentTime = Date.now()
        const timeElapsed = currentTime - lastJobOpportunityTime
        //refreshes once a week
        if (!viewedOpportunity.current || timeElapsed >= 1000 * 60 * 60 * 168) {
          getJobOpportunity()
          viewedOpportunity.current = true
        }
        const intervalId = setInterval(() => {
          getJobOpportunity()
        }, 1000 * 60 * 60 * 168 - timeElapsed)

        return () => {
            clearInterval(intervalId)
            viewedOpportunity.current = false
        }
    }, [])

    const infoSectionStyling = {
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        flexDirection:"column",
        margin:"4em auto 1em auto"
    }

    const messageContainerStyling = {
        background:"#fcf7eb",
        borderRadius:"10px",
        border:"0.5px solid #303e4d",
        padding:"0.5em 1em",
        boxShadow: "2px 2px 4px rgba(0, 0, 0, 0.2)",
        margin:"auto",
        fontSize:"0.9rem",
        minWidth:"280px",
        textAlign:"center"
    }

    const titleStyling = {
        fontWeight:"500",
        fontSize:"1.17rem"
    }

    const iconStyling = {
        fontSize:"1.5rem",
        color:"#303e4d"
    }

    const dividerStyling = {
        height:"2px",
        background:"#303e4d",
        margin:"3em auto",
        width:"90%",
        borderRadius:"15px"
    }

    const positionTitleStyling = {
        fontSize:"0.9rem",
        margin:"1em auto",
        fontWeight:"500"
    }

    const JobLinkStyling = {
        cursor:"pointer",
        color:"inherit",
        textDecoration:"underline"
    }

    return (
        <div className="motivate-page">
            <Header 
                secondButtonText={"MOTIVATE"}
                thirdButtonText={"LOG OUT"}  
            />
            <div className="motivate-info-section" style={infoSectionStyling}>
                <FontAwesomeIcon icon={faSun} style={iconStyling}/>
                <h4 style={titleStyling}>Daily Motivational Quote</h4>
                <div className="quote-container" style={messageContainerStyling}>
                {randomQuote && (
                    <>
                        <p>{randomQuote.quote} - {randomQuote.author}</p> 
                    </>
                )}
                </div>
                <div className="motivate-info-section-divider" style={dividerStyling}></div>
                <FontAwesomeIcon icon={faBolt} style={{...iconStyling, rotate:"-10deg"}}/>
                <h4 style={titleStyling}>Weekly Opportunity</h4>
                <TailSpin
                    visible={true}
                    height="80"
                    width="80"
                    color="#4fa94d"
                    ariaLabel="tail-spin-loading"
                    radius="1"
                    wrapperStyle={{}}
                    wrapperClass=""
                    />
                <div className="opportunity-container" style={messageContainerStyling}>
                    {jobOpportunity!=null ? (
                        <>
                        <h5 style={positionTitleStyling}>{jobOpportunity.role}</h5>
                        <p>@{jobOpportunity.company_name}</p>
                        <a 
                            href={jobOpportunity.url} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            style={JobLinkStyling}
                        >
                            <p>View Job Opportunity</p>
                        </a>
                        </>
                    )
                    :
                        <p>Try again later to view future Job Opportunities!</p>
                    }
                </div>
            </div>
        </div>
    )
}
