import React from "react";
import { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope, faLock } from "@fortawesome/free-regular-svg-icons"; // Import necessary icons

export default function LoginForm() {
    const [inputs, setInputs] = useState({});

    const handleChange = (event) => {
      const name = event.target.name;
      const value = event.target.value;
      setInputs(values => ({...values, [name]: value}));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log(inputs);
    };

    const containerStyling = {
        display: "flex",
        flexDirection: "column",
    };

    const mainTitleStyling = {
        textAlign: "left",
        margin: "3em auto 2em 0",
    };

    const buttonStyling = {
        background: "#303e4d",
        width: "200px",
        padding: "0.5em 1em",
        border: "none",
        borderRadius: "10px",
        color: "#fffdf8",
        fontFamily: "montserrat, sans-serif",
    };

    return (
        <div className="login-form-component" style={containerStyling}>
            <h3 style={mainTitleStyling}>Welcome Back!</h3>
            <form onSubmit={handleSubmit}>
                <label>Email</label>
                <div style={{ position: "relative" }}>
                    <input 
                        type="email" 
                        name="email"
                        placeholder="Email"
                        value={inputs.email || ""} 
                        onChange={handleChange}
                    />
                    <FontAwesomeIcon 
                        icon={faEnvelope} 
                        style={{ position: "absolute", top: "50%", right: "10px", transform: "translateY(-50%)" }} 
                    />
                </div>
                <label>Password</label>
                <div style={{ position: "relative" }}>
                    <input 
                        type="password" 
                        name="password"
                        placeholder="Password"
                        value={inputs.password || ""} 
                        onChange={handleChange}
                    />
                    <FontAwesomeIcon 
                        icon={faLock} 
                        style={{ position: "absolute", top: "50%", right: "10px", transform: "translateY(-50%)" }} 
                    />
                </div>
                <input type="submit" value="Login" style={buttonStyling}/>
            </form>
        </div>
    );
}
